import React from 'react'
import './Product.css'
import {useStateValue} from "./StateProvider";

function Product({id,title,price,rating,image}){

    const [{},dispatch] = useStateValue()

    const addToDasket = () => {
        //Add item to backek
        dispatch({
            type:'ADD_TO_BASKET',
            item:{
                id:id,
                title:title,
                image:image,
                price:price,
                rating:rating
            }
        })
    }
    return(
        <div className = "product">
            <div className="product__info">
                <p>{title}</p>
                <p className="product__price">
                    <small>$</small>
                    <strong>{price}</strong>
                </p>
                <div className="product__rating">
                    {
                        Array(rating)
                            .fill()
                            .map((_) => (
                                <p>‚≠ê</p>
                            ))
                    }
                </div>
            </div>

            <img src= {image} alt=""/>
            <button onClick={addToDasket}>Add To Cart</button>
        </div>
    )
}
export default Product
