import React from 'react';
import './Subtotal.css'
import CurrencyFormat from 'react-currency-format'

function Subtotal() {
    return (
        <div className="subtotal">
            {/*Price*/}
            <CurrencyFormat
                renderText={(value) => (
                    <>
                        <p>Subtotal </p>
                    </>
                )}
                decimalScale={2}
                value={0}
                displayType={"text"}
                thousandSeparator={true}
                prefix={"$"}
            />
            <button>Proceed to Checkout</button>
        </div>
    )

}
export default Subtotal
