import Product from "./Product";
import React from "react";


export const initialState = {
    basket:[{
        id:"12345",
        title:"SECOXGEAR EcoBoulder+ GDI-EXBLD810 Rugged Waterproof Floating",
        price: 199.99,
        rating:5,
        image:"https://m.media-amazon.com/images/I/81QtdeLlTQL._AC_UY218_.jpg"

    },],
    user:null
};

export const getBasketTotal = (basket) =>
    basket?.reduce((amount,item) => item.price + amount + amount, 0)

const reducer = (state, action) =>  {
    console.log(action);
    switch (action.type) {
        case 'ADD_TO_BASKET':
        //Logic for adding item to basket
            return {...state,
                basket:[...state.basket,action.item]
            };
            break;
        case 'REMOVER_FROM_BASKET':
            //Logic for Removing item from basket
            let newBasket = [...state.basket];

            const index = state.basket.findIndex((basketItem) => basketItem.id === action.id);

            if (index >= 0){
                //item exist in basket,remove it...
                newBasket.splice(index,1);
            }else{
                    console.log("can not remove")
            }
            return {...state,basket:newBasket}
        default:
            return state
    }
}
export default reducer;
