export const initialState = {
    basket:[],
    user:null
};

const reducer = (state, action) =>  {
    console.log(action);
    switch (action.type) {
        case 'ADD_TO_BASKET':
        //Logic for adding item to basket
            return {...state,
                basket:[...state.basket,action.item]
            };
            break;
        case 'REMOVER_FROM_BASKET':
            //Logic for Removing item from basket
            return {state};
            break;
        default:
            return state
    }
}
export default reducer;
